/**
 * min function
 *
 * @subpackage Math
 * @author shogo < shogo4405 at gmail dot com >
 * @version 0.0.1
 * @see http://www.php.net/min
 * @param  {Array} v
 * @param  {mixed} arg... 
 * @return {mixed}
 */
function min(v)
{
	if(arguments.length == 1)
	{
		if(v instanceof Array){ return min.apply(null, v); }
		throw new Error('wrong parameter for count');
	};
	var i, f, s = [], n = 0;
	function c(x, y){ return (y < x); };
	for(i=0,f=arguments.length;i<f;i++){
		if(c(arguments[n], arguments[i])){ n = i; };
	};
	return arguments[n];
};
